xf:load.resource:
javascript:createPlugin({
							id:'xformId',
							/*parentId:'pluginWraper',*/
							plugin:'extJsTree',
							proc:'plugin/extJsTree.py',
							postProcessProc:'plugin/handleExtJsTree.py',
							params:{
								title: 'Элементы дерева',
								listeners: {
									checkchange: {
										fn: function(node, checked, eOpts) {
											if (checked) {
												alert('CheckChange event. Select node '+node.get('text'));
											}
										}
									}
								}
							},
							options: {
								dataWidth: '500px',
								dataHeight: '450px',
								windowCaption: 'Выбор данных из дерева',
								onSelectionComplete: function(ok, plugin) {
									if (ok) {
										var records = plugin.getView().getChecked();
										names = [];
										Ext.Array.each(records, function(rec){
											names.push(rec.get('text'));
										});
										var a = xforms.defaultModel.defaultInstance.doc.getElementsByTagName('info')[0].getElementsByTagName('name')[0];
										setValue(a, names.join('|'));
										
										xforms.ready = false;
										xforms.refresh();
										xforms.ready = true;
									}
								}
							}
						});
